% decide the partition of the interval, i.e. left and right points of each panel
if Np<=8
    length_of_panel=(max_th-min_th)/Np;
    partition=min_th:length_of_panel:max_th;
else
    error('Number of panels should be no more than 8.');
end
%for each panel, fill out the abscissar and weights
abscissae=zeros(1,N*Np);
weights=zeros(1,N*Np);
for i=1:Np
    left_pt=partition(i);
    right_pt=partition(i+1);
    abscissae(N*(i-1)+1:N*i)=x*(right_pt-left_pt)/...
        2+(right_pt+left_pt)/2;
    weights(N*(i-1)+1:N*i)=w;
end
